// ==UserScript==
// @name         ProtonDB Peek
// @namespace    https://github.com/BlackRabbit22/ProtonDBPeek
// @version      1.0
// @description  Peek at the ProtonDB status of an app without leaving Steam.
// @author       BlackRabbit22
// @icon         https://www.protondb.com/sites/protondb/images/site-logo.svg
// @homepage     https://github.com/BlackRabbit22/ProtonDBPeek
// @match        *://store.steampowered.com/app/*
// @grant        GM_xmlhttpRequest
// ==/UserScript==

// get appID from URL
const appID = window.location.href.split("/")[4];

// SVG badges encoded to base64
const tierBadges = {
  platinum:
    "data:image/svg+xml;base64,",
  gold: 
    "data:image/svg+xml;base64,",
  silver:
    "data:image/svg+xml;base64,",
  bronze:
    "data:image/svg+xml;base64,",
  borked:
    "data:image/svg+xml;base64,",
  pending:
    "data:image/svg+xml;base64,",
  unverified:
    "data:image/svg+xml;base64,",
};

// add HTML elements to page
const decorate = (tierBadge, protonDBHref, reviewCount) => {
  let protonTier = document.createElement("div");
  protonTier.innerHTML = `
  <div id="protonDB" class="dev_row">
      <div  class="subtitle column">ProtonDB:</div>
      <div data-tooltip-html="Total reviews: ${reviewCount}" class="summary column">
          <a href="${protonDBHref}" target="_blank">
              <img src="${tierBadge}" />
          </a>
      </div>
  </div>`.trim();
  document.getElementsByClassName("user_reviews")[0].appendChild(protonTier.firstChild);
};

// get tier from protondb API
const getTier = (appID) => {
  const url = `https://www.protondb.com/api/v1/reports/summaries/${appID}.json`;

  return new Promise((resolve, reject) => {
    GM_xmlhttpRequest({
      method: "GET",
      url: url,
      onload: (response) => {
        if (response.status == 404) resolve(["unverified", "0"]);
        resolve([JSON.parse(response.responseText).tier, JSON.parse(response.responseText).total]);
      },
      onerror: reject,
    });
  }).catch((error) => console.log(error));
};

getTier(appID)
  .then((callback) => {
    const reviewCount = callback[1];
    const tierBadge = tiers[callback[0]];
    const protonDBHref = `https://www.protondb.com/app/${appID}`;

    decorate(tierBadge, protonDBHref, reviewCount);
  })
  .catch((e) => void e);
